import { Component, OnInit } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { SidenavService } from 'src/app/shared/services/sidenav.service';

import { CandidateService } from 'src/app/store/services/candidate.service';

interface pathParam {
  office: string; 
  seatType?: string;
  seatName?: string; 
}

@Component({
  selector: 'app-office',
  templateUrl: './office.component.html',
  styleUrls: ['./office.component.scss'],
})
export class OfficeComponent implements OnInit {

  office: string;
  seatName: string;
  selectedCandidateId: string;
  candidateIds: string[] = [];
  isExpanded: boolean = false;
  officeUrl: string;

  constructor(
    private route: ActivatedRoute,
    private activatedRoute: ActivatedRoute,
    private router: Router,
    private sidenavService: SidenavService,
    private candidateService: CandidateService,
  ) { }

  setCandidateCards(): void {
    this.candidateService.getCandidates(
      { office: this.office, district: '', year: '2020' }
    ).subscribe(candidates => {
      this.candidateIds = candidates.map(candidate => candidate.id);
    })
  }

  setComponentData() {
    this.sidenavService.changeSelectedOffice( this.office );  
    this.isExpanded = false;
  }

  ngOnInit(): void {
    this.route.data.subscribe(data => {
      this.office = data.officePath.split('-').join(' ');
      this.officeUrl = data.officePath;

      this.setComponentData(); 
      this.setCandidateCards();
    })

    this.route.paramMap.subscribe(params => {
      const candidateId = params.get('candidateId')
      if (candidateId) this.setCandidate(candidateId);
    })
  }

  setCandidate(candidateId: string) {
    this.sidenavService.changeSelectedCandidate(candidateId);

    this.isExpanded = true;
    this.selectedCandidateId = candidateId;
  }

  onOpenExpanded(candidateId: string) {
    this.router.navigate([`/office/${this.officeUrl}`, candidateId], { relativeTo: this.route });
  }

  onCloseExpanded() {
    this.router.navigate([`/office/${this.officeUrl}`], { relativeTo: this.route });
  }
}

import { Component, Input, OnChanges } from '@angular/core';

import { EChartsOption, ECharts } from 'echarts';

import { getCompactFormattedCurrency } from '../../shared/number-formatter'

export interface RaisedByIndustry {
  name: string,
  value: number,
  label?: string,
}

@Component({
  selector: 'app-raised-by-industry-bar',
  templateUrl: './raised-by-industry-bar.component.html',
  styleUrls: ['./raised-by-industry-bar.component.scss']
})
export class RaisedByIndustryBarComponent implements OnChanges {

  @Input() raisedByIndustries: RaisedByIndustry[];

  private dataRowsCount = 0;

  echartsInstance: ECharts;
  mergeOption: EChartsOption;
  initOpts: EChartsOption;
  
  chartOption: EChartsOption = {
    grid: {
      containLabel: true,
      left: '5%',
      right: '5%',
      top: '2%',
      bottom: '2%',
    },
    tooltip: {
      show: true,
      trigger: 'axis',
      formatter: (params) => 
        `${params[0].data.name}: $${params[0].data.value.toLocaleString()}`,
      axisPointer: {
        type: 'shadow',
      }
    },
    dataset: {
      source: [],  // set in ngOnChanges
    },
    xAxis: {
      position: 'top',
      axisLabel: {
        formatter: (value: number) => 
          getCompactFormattedCurrency(value),
      },
    },
    yAxis: {
      type: 'category',
      axisTick: {
        show: false
      },
    },
    series: [{
      type: 'bar',
      name: 'raised-by-industry-bar',
      encode: {
        y: 'name',
        x: 'value',
        label: 'label',
      },
      label: {
        show: true,
        position: 'right',
        formatter: (params) => 
          getCompactFormattedCurrency(params.data['value'], 1),
      },
      itemStyle: {
        color: '#53AE79',
      },
      barCategoryGap: '120%',
      barMinWidth: 16,
    }],
  }

  constructor() {  }

  ngOnChanges(): void { 
    this.setChartMergeOption();
    this.setChartHeight();
  }

  onChartInit(ec: ECharts): void {
    this.echartsInstance = ec;
  }

  setChartMergeOption(): void {
    this.mergeOption = {
      dataset: {
        source: this.raisedByIndustries,
      }
    };
  }

  setChartHeight(): void {
    const perItemHeight = 35;
    const topOffset = 80;
    const itemCount = this.raisedByIndustries.length;

    if (this.dataRowsCount === itemCount) return;

    const height = itemCount * perItemHeight + topOffset;

    if (this.echartsInstance) {
      this.echartsInstance.resize({ height });
    } else {
      this.initOpts = { height };
    }

    this.dataRowsCount = itemCount;
  }

}

