    justify-content: center;
    text-align: center;
  }

  .average-donation {
    grid-column: 4;
    div {
      padding: 10px;
    }
  }

  .raised, .donors {
    grid-column: 3;
    div {
      padding: 5px;
    }
  }
}

@media (min-width: 1500px) {
  .details-header-wrapper {
    grid-template-rows: 1fr 1fr;
  }
  .image {
    grid-row: 1 / 2;
    display: flex;
    flex-direction: row;
    justify-content: center;

    img {
      max-width: 40%;
    }
  }
  .info {
    grid-column: 1;
