  @Input() inside: number;
  @Input() outside: number;

  mergeOption: EChartsOption = {};

  chartOption: EChartsOption = {
    tooltip: {
      show: true,
      trigger: 'item',
      formatter: (params) => `Raised ${params.name}:\n $${(+params.value).toLocaleString()}`,
    },
    series: [
      {
        type: 'pie',
        clockwise: false,
        label: {    
          formatter: (params) => 
              `{a|${params.data['name']}}\n` +
              `{b|${getFormattedCurrency(+params.data['value'], 1)}}`,
          rich: {
            a: {
              fontSize: '1em',
