        labelLayout: {
          moveOverlap: 'shiftY',
        },
        emphasis: {
          itemStyle: { opacity: .5, },
        },        
        radius: ['50%', '90%'],
      },
    ],
  };

  constructor() { }

  ngOnChanges(): void {
    this.setChartMergeOption();
  }

  setChartMergeOption(): void {
   
    this.mergeOption = {
      series: [{
        data: [
          {
            name: 'Inside',
            value: this.inside ? this.inside : 0,
            color: '#3392ff',
            itemStyle: { color: '#3392ff' },
            label: { position: 'center', }
          },
          {
            name: 'Outside',
            value: this.outside ? this.outside : 0,
            color: '#bfd63b',
            itemStyle: { color: '#bfd63b' },
            label: { 
              position: 'inner', 
              textBorderColor: '#bfd63b',
              textBorderWidth: 2,
              textShadowColor: '#bfd63b',
              textShadowBlur: 8,
            }
          },
        ],
      }]
    };

  }
}

