import { Injectable } from '@angular/core';
import { Observable, } from 'rxjs';
import { map, toArray, mergeMap, filter, } from 'rxjs/operators';

/**
 * candidate-data.service: This service obtains data from the candidate-store.service and 
 *  provides the application with access to the data. This service is intended to be used 
 *  as an intermediatory for accessing the source data used in the application. Data 
 *  provided by this service is in the form of observables that match specific interfaces.
 * 
 */

import { CandidateStoreService } from './candidate-store.service';

import { CandidateNavigation } from '../interfaces/candidateNavigation'
import { CandidateCard } from '../interfaces/candidateCard';

import { RaisedInOut } from '../vv-charts/interfaces/raisedInOut';
import { OutsideMoney } from '../vv-charts/interfaces/outsideMoney';
import { DonationsByGroup } from '../vv-charts/interfaces/donationsByGroup';


@Injectable({
  providedIn: 'root',
})
export class CandidateDataService {
  private areaName = 'City of San Diego';
  private defaultJurisdiction = 'City';

  constructor(public CandidateStore: CandidateStoreService) { }

  getCandidates(): Observable<CandidateNavigation> {
    
    return this.CandidateStore.getCandidateList().pipe(
      toArray(),
      mergeMap(candidates => {
        return candidates.sort((firstCandidate, secondCandidate) => {
          const firstCandidateName = firstCandidate.fullName.toLowerCase();
          const secondCandidateName = secondCandidate.fullName.toLowerCase();
    
          if (firstCandidateName < secondCandidateName) return -1;
          if (firstCandidateName > secondCandidateName) return 1;
          return 0;
        });
      }),
    )

  }

  getCandidateCards(office?: string, seat?: string): Observable<CandidateCard> {

    return this.getCandidates().pipe(
      filter(candidate => 
        office ? candidate.officeType.toLowerCase() === office.toLowerCase() : true
      ),
      filter(candidate => 
        seat ? candidate.seat.name.toLowerCase() === seat.toLowerCase() : true
      ),
      mergeMap(candidate => this.CandidateStore.getCandidateExpandedData(candidate.id).pipe(
        map(expandedData => ({candidate, expandedData}))
      )),
      mergeMap( ({candidate, expandedData})  => this.CandidateStore.getCandidateImageUrl(candidate.id).pipe(
        map(imageUrl => ({candidate, expandedData, imageUrl}))
      )),
      map( ({candidate, expandedData, imageUrl}) => ({
        id: candidate.id,
        name: candidate.fullName,
        description: expandedData['description'],
        raised: Number(expandedData['raised vs spent'][0]['Raised']),
        donors: Number(expandedData['raised vs spent'][0]['Donors']),
        candidateImgURL: imageUrl,
      }))
    );

  }

  getRaisedInOutChart(id: string): Observable<RaisedInOut> {

    return this.CandidateStore.getCandidate(id).pipe(
      mergeMap(candidate => this.CandidateStore.getCandidateExpandedData(candidate.id).pipe(
        map(expandedData => ({candidate, expandedData}))
      )),
      map( ({candidate, expandedData}) => ({
        id: candidate.id,
        inside: Number(expandedData["in vs out district"][0]['in']),
        outside: Number(expandedData["in vs out district"][0]['out']),
        areaName: this.areaName,
        jurisdiction: candidate?.seat ? candidate.seat.type : this.defaultJurisdiction,
        jurisdictionSuffix: candidate?.seat?.name,
      }))
    );

  }

  getOutsideMoneyChart(id: string): Observable<OutsideMoney>  {

    return this.CandidateStore.getCandidate(id).pipe(
      mergeMap(candidate => this.CandidateStore.getCandidateExpandedData(candidate.id).pipe(
        map(expandedData => ({candidate, expandedData}))
      )),
      map( ({candidate, expandedData}) => ({
        id: candidate.id,
        support: Number(expandedData["oppose"]),
        oppose: Number(expandedData["support"])
      }))
    );

  }

  getDonationsByGroupChart(id: string): Observable<DonationsByGroup> {

    return this.CandidateStore.getCandidate(id).pipe(
      mergeMap(candidate => this.CandidateStore.getCandidateExpandedData(candidate.id).pipe(
        map(expandedData => ({candidate, expandedData}))
      )),
      map( ({candidate, expandedData}) => {
        const groups = Object.values(expandedData["by industry"][0]).map(group => 
          ({
            name: group[0],
            amount: Number(group[1]),
            percent:Number( group[2])
          })
        );

        return { id: candidate.id, groups }
      })
    );

  }

}

import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { CandidateTree } from '../candidate';

@Injectable({
  providedIn: 'root'
})
export class CandidateService {

  constructor(public http: HttpClient) { }

  getCampaignTotals(): Observable<any> {
    const campaignTotalsFilePath = "assets/candidates/2020/campaign_race_totals.json";

    const campaignTotals = this.http.get<any>(campaignTotalsFilePath)
      .pipe( // transform the property names used from those in the json file
        map(result => {
          return {
            'mayor': result['mayor'],
            'cityAttorney': result['city attorney'],
            'cityCouncil': result['city council']
          }
        })
      );

    return campaignTotals;
  }

  getCampaignTotalsByOffice(office: string): Observable<any> {
    return this.getCampaignTotals().pipe(
      map(offices => offices[office])
    )
  }

  getAll() {
    return this.http.get("assets/candidates/2020/candidates.json").toPromise();
  }

  getNumberOfCandidates(): Observable<any> {
    const campaignCandidateTotalsFilePath = "assets/candidates/2020/campaign_candidate_totals.json";

    const candidateCounts = this.http.get<any>(campaignCandidateTotalsFilePath)
      .pipe( // transform the property names used from those in the json file
        map(result => {
          return {
            'mayor': result['mayor'],
            'cityAttorney': result['city attorney'],
            'cityCouncil': result['city council']
          }
        })
      );

    return candidateCounts;
  }

  getNumberOfCandidatesByOffice(office: string): Observable<any> {
    return this.getNumberOfCandidates().pipe(
      map(offices => offices[office])
    )
  }

  // Mayoral Candidates
  getMayors() {
    return this.getAll().then(
      (all: Record<string, CandidateTree>) => Promise.all(
        Object.values(all["mayor"].candidates).map(url => this.http.get(url).toPromise())
      )
    );
  }

  // City Attorney Candidates
  getCityAttorneys() {
    return this.getAll().then(
      (all: Record<string, CandidateTree>) => Promise.all(
        Object.values(all["city-attorney"].candidates).map(url => this.http.get(url).toPromise())
      )
    );
  }

  // City Council Candidates
  getCityCouncilorsByDistrict(districtNumber: string) {
    let districtName = `city-council-district-${districtNumber}`;

    return this.getAll().then(
      (all: Record<string, CandidateTree>) => Promise.all(
        Object.values(all[districtName].candidates).map(url => this.http.get(url).toPromise())
      )
    );
  }

  getLastUpdated(): Observable<any> {
    const campaignTotalsFilePath = "assets/candidates/2020/campaign_race_totals.json";

    const lastUpdate = this.http.get<any>(campaignTotalsFilePath)
      .pipe( // transform the property names used from those in the json file
        map(result => result["last update"])
      );

    return lastUpdate;
  }
}

import { HttpClient, } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable, of, from, iif } from 'rxjs';
import { first, map, mergeMap } from 'rxjs/operators';

/**
 * candidate-store.service: Provides access to data sources so that the dependents of this service 
 *  can use the data without needing to know its source. Current data sources are imported .ts files 
 *  and JSON files. Future sources will include data from an API. Data provided by this service is 
 *  in the form of observables that match specific interfaces. The application's components should
 *  not directly access this service. This service is intended to be primarily accessed by the 
 *  candidate-data.service.
 *  
 */

import { CandidateNavigation } from '../interfaces/candidateNavigation';
import { CandidateJSON } from '../interfaces/candidateJSON';

import { candidateNavigationItems } from '../../assets/candidates/mock-candidateNavigationItems';
import { candidateAssets } from '../../assets/candidates/mock-candidateAssetPaths';


@Injectable({
  providedIn: 'root',
})
export class CandidateStoreService {
  private defaultImagePath = 'assets/candidate-card/profile.png';

  constructor(public http: HttpClient) { }

  private hasCalculationsPath(navigationItem: CandidateNavigation): boolean {

    return candidateAssets
      .some(asset => asset.id === navigationItem.id && asset.calculationsPath.length > 0);

  }

  getCandidateList(): Observable<CandidateNavigation> {

    return from(candidateNavigationItems).pipe(
      mergeMap(
        navigationItem => iif(
          () => this.hasCalculationsPath(navigationItem),
          of(navigationItem),
        )
      ),
    );

  }

  getCandidate(id: string): Observable<CandidateNavigation> {
    
    return from(candidateNavigationItems).pipe(
      first(candidate => candidate.id === id),
    );

  }

  getCandidateExpandedData(id: string): Observable<CandidateJSON> {

    return from(candidateAssets).pipe(
      first(candidate => candidate.id === id),
      mergeMap(candidate => this.http.get<CandidateJSON>(candidate.calculationsPath)),
    );

  }

  getCandidateImageUrl(id: string): Observable<string> {

    return from(candidateAssets).pipe(
      first(candidate => candidate.id === id),
      map(candidate => 
        candidate.imagePath.length > 0 
          ? candidate.imagePath 
          : this.defaultImagePath
      ),
    );

  }

}

import { Injectable } from '@angular/core';
import { Subject } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class SidenavService {
  emitCandidateKeyFromSidenav = new Subject<any>();
  emitCandidateNameFromCard = new Subject<any>();
  emitCandidateTypeFromSplash = new Subject<any>();

  candidateKeyEmittedFromSidenav$ = this.emitCandidateKeyFromSidenav.asObservable();
  candidateNameEmittedFromCard$ = this.emitCandidateNameFromCard.asObservable();
  candidateTypeEmittedFromSplash$ = this.emitCandidateTypeFromSplash.asObservable();

  constructor() { }

  emitCandidateKeySidenav(key: string) {
    this.emitCandidateKeyFromSidenav.next(key);
  }

  emitCandidateNameCard(key: string) {
    this.emitCandidateNameFromCard.next(key);
  }

  emitCandidateTypeSplash(key: string) {
    this.emitCandidateTypeFromSplash.next(key);
  }
}

