@import "../../styles/variables";
@import "../../styles/elements";
@import "../../styles/global";
.router-link-active {
  color: $vvyellow !important;
}

.main_section{
  background-color: $vvbackground;
}
.full-width{
  padding: 0px;
}
.logo{
  &:focus{
    outline: none !important;
  }
}
.sidenav-container {
  height: 100vh;
  border: 1px solid rgba(0, 0, 0, 0.5);

  .mat-accordion {

    .mat-expansion-panel {
      border-radius: 0px !important;
      box-shadow: none !important;
      border-bottom: 1px solid #F5F5F5;

      .mat-expansion-panel-content {
        background: lighten($vvbackground, 3%);
        .mat-expansion-panel-body {
          padding: 0 20px 0;
          margin: 0 10px;
        
        }
      
        i {
          opacity: 0.7;
        }

        .mat-expansion-panel-header {
          .mat-content{
            display: flex;
            justify-content: space-between;
            //margin: 0;
          }

          .mat-expansion-indicator {
            padding: 0 5px;
          }
          transition: none;
          background: lighten($vvbackground, 3%);
          padding: 5px 0px;

          &:hover {
            background: lighten($vvbackground, 3%) !important;
            border-bottom: 2px $vvlightblue solid;
            font-style: italic;
          }

        }
      }
    }

    .active-link {
      background-color: $vvlightblue !important;
      

      .mat-content .mat-expansion-panel-header-title {
        color: $vvlightgrey;         
      }
    }

    .mat-expansion-panel-header {
      transition: 300ms ease;
      .mat-content .mat-expansion-panel-header-title {
        text-transform: uppercase;
        font-weight: bold;
      }

      &:hover {
        background-color: lighten($vvlightblue, 10%) !important;

        .mat-content .mat-expansion-panel-header-title {
          color: $vvlightgrey;         
        }
      }
      
    }

    .mat-expansion-panel-body {
      ul {
        list-style-type: none;
        padding: 0px;

        li {
          margin: 0 0 5px 0;

          &:hover {
            font-weight: bold;
            color: $vvlightblue;
            cursor: pointer;
          }
        }
      }
    }

    .candidate-selected {
      font-weight: bold;
      color: $vvlightblue;
    }

    .district-selected {
      background: lighten($vvbackground, 3%) !important;
      border-bottom: 2px $vvlightblue solid;
      font-style: italic;
    }
}

  .submenu {
    overflow-y: hidden;
    transition: transform 300ms ease;
    transform: scaleY(0);
    transform-origin: top;
    padding-left: 30px;
    background: $vvlightgrey;
  }

  .submenu.expanded {
    transform: scaleY(1);
  }

  .menu-button-expand {
    transition: 300ms ease-in-out;
    transform: rotate(0deg);
  }

  .menu-button-expand.rotated {
    transform: rotate(180deg);
  }

  mat-nav-list {
    width: 15vw;
    padding-top: 0;
  }
}

:host ::ng-deep .mat-list-item-content {
  font-size: 20px;
  font-weight: 500;
}

@media (max-width: 1200px) {

  .header-nav{
    .bar-toggle{
      display:block !important;
      color:#fff;
      padding-right:20px;
      font-size:22px;
    }
    grid-template-columns: auto 3fr auto auto !important;
  }
  .sidenav-container {
    mat-drawer {
