    margin-right: 40px;
    margin-bottom: 20px;
  }
}

.footer_links {
  margin: 20px 20px 20px 0px;
  display: inline-block;
  

  font-weight: 400;
  font-size: 14px;
  line-height: 1.5;
  text-decoration: none;
  color: $vvblue;



.footer_logo {
  align-self: center;
  justify-self: center;
  margin-right: 20px;
}
.updated{
 color: $vvgreen;
 font-style: italic;
 font-size: 12px;
}

/*Hover States*/

.subheadline p a:hover,
.nav_links:hover,
