  }
}

.ftm{
  margin: 20px auto 20px auto;
  text-align:center;
  padding:40px 40px 60px 40px;
  font-weight: 100;
  box-shadow: $vvshadow;
  color:#4E4E4E;
  background-color: #fff;
  h1{
    margin: 0px;
  }
  p{
    margin:10px 0px 20px 0px;
    line-height: 1.4;
  }
  .ftm_caption{
   
    display: block;
    margin:10px auto 20px auto;
  }
  .choose{
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    align-content: center;
    

    div{
      border-right:2px solid $vvbackground;
    }
    div:last-child{
      border-right:none;
    }


      .cta{

          @include button-styles;
          padding: 10px 20px;
          max-width: 200px;
          text-decoration: none;
          a{
            text-decoration: none;
          }
          fa-icon{
            padding: 0px;
            margin: 0px 4px 0px 0px;
          }
      }
      .cta:hover{
            background-color: $vvlightblue;
        }
}

    .racename{
        font-size: 20px;
        margin-bottom: 30px;
     
  }
  .raised{
    margin:40px 0px  5px 0px;
  }
  .candidate_num{
    margin:10px 0px  5px 0px;
  }
  h3{
    font-size:20px;
    margin: 0px;
  }
  .amount_raised{
    color:#289A58;
  }
  
}

.sources{
  margin-top:60px;
  font-size:14px;
  a{
    color:$vvlightblue;
  }
  p{
    margin:0px !important;
  }
}

@media only screen and (max-width: 1000px) {

  .goals{
    grid-template-columns: 1fr 1fr!important;
    row-gap: 20px;
    max-width:500px;
   
  }
  .choose{
    grid-template-columns: 1fr !important;
    row-gap: 20px;
    div{
      border-bottom:2px solid $vvbackground !important;
      border-right:none!important;
      padding-bottom: 40px;
    }
    div:last-child{
      border-bottom:none!important;
      padding-bottom: 0px;
    }
  }
  
}

@media only screen and (max-width: 400px) {


  .goals{
    grid-template-columns: 1fr!important;
    row-gap: 20px;
    max-width:500px;
   
  }


}

